import React, { useState, useEffect } from 'react';
import { 
  StyleSheet, 
  Text, 
  View, 
  Image, 
  ActivityIndicator, 
  ScrollView,
  Dimensions
} from 'react-native';
import { useRoute } from '@react-navigation/native';

const { width } = Dimensions.get('window');

export default function ResultScreen() {
  const route = useRoute();
  const { photo } = route.params;
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    analyzeImage();
  }, []);

  const analyzeImage = async () => {
    try {
      // Simulated API call delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Example analysis result
      setAnalysis(`
        Phân tích chỉ tay của bạn:

        1. Đường Sinh mệnh (Life Line):
        - Đường sinh mệnh của bạn khá dài và rõ ràng
        - Điều này cho thấy bạn có sức khỏe tốt và tuổi thọ cao
        - Có một số nhánh nhỏ thể hiện sự thay đổi trong cuộc sống

        2. Đường Tình duyên (Heart Line):
        - Đường tình duyên sâu và rõ nét
        - Thể hiện bạn là người chung thủy và tình cảm
        - Có khả năng gặp được người phù hợp trong 2-3 năm tới

        3. Đường Sự nghiệp (Career Line):
        - Đường sự nghiệp của bạn có nhiều nhánh
        - Thể hiện bạn sẽ có nhiều cơ hội phát triển trong công việc
        - Có thể sẽ thay đổi nghề nghiệp 1-2 lần trong tương lai

        4. Đường Trí tuệ (Head Line):
        - Đường trí tuệ dài và thẳng
        - Cho thấy bạn là người thông minh, logic
        - Có khả năng phân tích và giải quyết vấn đề tốt

        5. Các đường phụ:
        - Có một số đường nhỏ thể hiện tài lộc
        - Khả năng có thu nhập tốt từ nhiều nguồn khác nhau
        - Nên đầu tư và kinh doanh trong 2 năm tới

        Lời khuyên:
        - Tập trung phát triển kỹ năng chuyên môn
        - Duy trì lối sống lành mạnh
        - Mở rộng các mối quan hệ xã hội
        - Cân nhắc kỹ các quyết định tài chính
      `);
      setLoading(false);
    } catch (error) {
      setAnalysis('Có lỗi xảy ra khi phân tích ảnh. Vui lòng thử lại.');
      setLoading(false);
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.imageContainer}>
        <Image 
          source={{ uri: photo.uri }} 
          style={styles.image} 
          resizeMode="contain"
        />
      </View>
      <View style={styles.resultContainer}>
        {loading ? (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#4a90e2" />
            <Text style={styles.loadingText}>Đang phân tích chỉ tay...</Text>
          </View>
        ) : (
          <View style={styles.analysisContainer}>
            <Text style={styles.analysisText}>{analysis}</Text>
          </View>
        )}
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  imageContainer: {
    width: width,
    height: width * 0.8,
    backgroundColor: '#f0f0f0',
    marginBottom: 20,
  },
  image: {
    width: '100%',
    height: '100%',
  },
  resultContainer: {
    padding: 20,
  },
  loadingContainer: {
    alignItems: 'center',
    padding: 20,
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  analysisContainer: {
    backgroundColor: '#f8f9fa',
    borderRadius: 10,
    padding: 20,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  analysisText: {
    fontSize: 16,
    lineHeight: 24,
    color: '#333',
  },
});
